###This code will indicate the consequences for provided variations (mutAlt vector) at their respective positions (mutPos vector)
###Note this does not take into account start or stop losses

#read in data (position and alt)

#mutPos <- as.integer(readClipboard())
#mutAlt <- readClipboard()

library("Biostrings")

lacZ <- ""

codons <- vector()
startPos <- vector()

#####extract the codons and their start position
i = 1
while (i <= nchar(lacZ)) {

	codons <- c(codons,substr(lacZ, i, i+2))
	startPos <- c(startPos, i)
	i = i + 3

}


#####create codons for each mutation

consequence <- vector()
refCodonList <- vector()
altCodonList <- vector()
refAA <- vector()
altAA <- vector()

for (j in 1:length(mutPos)) {

	#codon comparison
	
	currentCodon <- codons[ceiling(mutPos[j]/3)]
	mutCodon <- currentCodon

	if (mutPos[j] %% 3 == 0) {
	substr(mutCodon, 3, 3) = mutAlt[j]		
	}

	if (mutPos[j] %% 3 == 2) {
	substr(mutCodon, 2, 2) = mutAlt[j]		
	}

	if (mutPos[j] %% 3 == 1) {
	substr(mutCodon, 1, 1) = mutAlt[j]		
	}

	if (GENETIC_CODE[[mutCodon]] == "*") {
	consequence <- c(consequence, "nonsense")
	}
	
	if (GENETIC_CODE[[mutCodon]] != GENETIC_CODE[[currentCodon]] && GENETIC_CODE[[mutCodon]] != "*") {	
	consequence <- c(consequence, "missense")
	}

	if (GENETIC_CODE[[mutCodon]] == GENETIC_CODE[[currentCodon]]) {
	consequence <- c(consequence, "silent")
	}

refCodonList <- c(refCodonList, currentCodon)
altCodonList <- c(altCodonList, mutCodon)
refAA <- c(refAA, GENETIC_CODE[[currentCodon]])
altAA <- c(altAA, GENETIC_CODE[[mutCodon]])


}

mutOutput <- cbind(consequence, refCodonList, altCodonList, refAA, altAA)

write.table(mutOutput, "clipboard", sep="\t", row.names=FALSE, col.names=FALSE)
